          item.is_active ? 'Active' : 'Inactive'
        ];
      case 'admission-requirements':
        return [
          item.category_display || item.category || 'N/A',
          item.requirement_text || 'N/A',
          item.is_active ? 'Active' : 'Inactive'
        ];
